export const App = () => {
  return (
    <>
      <Nabvar>
        <Logo>TODO</Logo>
        <NavItems>
          <NavItem>
            <Link to="/lists">Todos</Link>
          </NavItem>
          <NavItem>
            <Link to="/lists/create">Add New Todo</Link>
          </NavItem>
        </NavItems>
      </Nabvar>
      <Wrapper>
        <Switch>
          <Route exact path="/lists" component={TodoList} />
          <Route exact path="/lists/create" component={AddTodo} />
          <Route path="/lists/:id/edit" component={EditTodo} />
        </Switch>
      </Wrapper>
    </>
  );
};

export default App;
